function SingleColor({rgb, weight, index}) {
    const [alert, setAlert] = useState(false)
    const bcg = rgb.join(',')
    console.log(bcg)
  return (
    <>
        <div className='row'>
            <div className='col item'  style={{backgroundColor: `rgb(${bcg})`}}>
                <p>{weight}</p>
            </div>
        </div>
    </>
  )
}

export default SingleColor



