import React, {useState} from 'react'

function SingleColor({rgb, weight, index, hex}) {
    const [alert, setAlert] = useState(false)
    const bcg = rgb.join(',')
    console.log(bcg)
  return (
    <>
        <div className='item p-3'  style={{backgroundColor: `rgb(${bcg})`}}>
            <p>{weight}%</p>
            <p>#{hex}</p>
        </div>
    </>
  )
}

export default SingleColor
